package com.example.recyclerview;

import androidx.appcompat.app.AppCompatActivity;
import androidx.recyclerview.widget.GridLayoutManager;
import androidx.recyclerview.widget.LinearLayoutManager;
import androidx.recyclerview.widget.RecyclerView;

import android.os.Bundle;

import java.util.ArrayList;

public class MainActivity extends AppCompatActivity {

    RecyclerView recyclerView;
    ArrayList<film> filmList;
    RecyclerView.LayoutManager linearLayoutManager,gridLayoutManager;
    Adapter filmlistadapter;

    @Override
    protected void onCreate(Bundle savedInstanceState) {
        super.onCreate(savedInstanceState);
        setContentView(R.layout.activity_main);

        recyclerView = findViewById(R.id.recyclerViewFilm);

        filmList = new ArrayList<>();
        filmList.add(new film("https://upload.wikimedia.org/wikipedia/ru/thumb/b/b0/Maleficent_-_Mistress_of_Evil_%28poster%29.jpg/202px-Maleficent_-_Mistress_of_Evil_%28poster%29.jpg","Malefisenta", "2014"));
        filmList.add(new film("https://avatars.mds.yandex.net/get-kinopoisk-image/1773646/279f9f19-6a29-4fae-935e-54ddf80c653b/orig","Klaus", "2019"));
        filmList.add(new film("https://upload.wikimedia.org/wikipedia/en/8/8b/We_Can_Be_Heroes_2020_film_poster.png","We can be Heroes", "2020"));
        filmList.add(new film("https://images.moviesanywhere.com/af475dd305b0b5ea2d2d8ecb346f9052/05663687-c4ec-4e30-87ed-a9bf9f59117c.jpg","Fantasy Island", "2021"));
        filmList.add(new film("data:image/jpeg;base64,","Searching", "2018"));

        linearLayoutManager = new LinearLayoutManager(this);
        gridLayoutManager = new GridLayoutManager(this,3);

        recyclerView.setLayoutManager(linearLayoutManager);
        recyclerView.setHasFixedSize(true);

        filmlistadapter = new Adapter(this,filmList);
        recyclerView.setAdapter(filmlistadapter);


    }
}